package com.mail.filemonitor;import org.apache.commons.io.monitor.FileAlterationListener;import org.apache.commons.io.monitor.FileAlterationMonitor;import org.apache.commons.io.monitor.FileAlterationObserver;import java.io.File;/** * Copyright © CapRobin * <p> * Name：ReYoFileMonitor * Describe：文件夹及其文件操作监视器 * Date：2021-05-26 10:54:33 * Author: YuFarong CapRobin@yeah.net */public class FileMonitor {    FileAlterationMonitor monitor = null;    public FileMonitor(long interval) throws Exception {        monitor = new FileAlterationMonitor(interval);    }    public void monitor(String path, FileAlterationListener listener) {        FileAlterationObserver observer = new FileAlterationObserver(new File(path));        monitor.addObserver(observer);        observer.addListener(listener);    }    public void stop() throws Exception {        monitor.stop();    }    public void start() throws Exception {        monitor.start();    }}